		/**
		* Get string of html for a given array
		*
		* @param array $array The array
		*
		* @return string
		*/
			public function arrayToListHTML(array $array = []): string{
				
				$r = '<ul>';
				
				if(!empty($array)){
					
					foreach ($array as $fol => $contents){
						
						$folBits = explode('/', $fol);
						$folHere = end($folBits);
						
						$r .= '<li data-jstree=\'{"icon":"fa fa-folder"}\' data-dir="'.urlencode($fol.'/').'" id="'.urlencode($fol.'/').'"> '.$folHere;
						
						if(!empty($contents)){
							$r.= $this->arrayToListHTML($contents);
						}
						
						$r .= '</li>';
						
					}
					
				}
				
				$r .= '</ul>';
				
				return $r;
				
			}
		
