        /**
         * Get the files and folders within a directory
         * 
         * @param string $directory  
         * 
         * @return array
         */
			public function getDirectoryContents(string $directory = ''): ?array
			{
				
				/* Returns array of files + folders */
				
				//if no directory poassed, revert to root
				if ($directory === ''){
					$directory = DOC_ROOT.$this->_root_directory;
				}
				
				//scan root folder...
				$directoryArray = @scandir($directory);
				
				if ($directoryArray === false){
					//not a directory
					throw new \Exception('Directory Requested Not Valid.');
				}
				
				//set up arrays for files and folders
				$fileArray = array();
				$folderArray = array();
				
				foreach ($directoryArray as $objectName){
					
					if (is_dir($directory.$objectName) && !preg_match('#^[\.]+$#', $objectName)){
						
						$folderArray[] = $objectName;
						
					} else {
						
						//if level up markers
						if (preg_match('#^[\.]+$#', $objectName) || $objectName == '.htaccess'|| $objectName == '.ftpquota'){
							//ignore
						} else {
							$fileArray[] = $objectName;
						}
						
					}
					
				}
				
				//sort arrays into alphabetical
				sort($fileArray);
				sort($folderArray);
				
				return ['folders'=>$folderArray,'files'=>$fileArray];
				
			}
			
		public function getDirArray($dir){
			
			$contents = [];
			
			foreach (@scandir($dir) as $node) {
				
				if ($node == '.' || $node == '..'){
					continue;
				}
				
				if (is_dir(rtrim($dir, '/') . '/' . trim($node, '/'))) {
					
					$contents[rtrim($dir, '/').'/'.trim($node, '/')] = $this->getDirArray(rtrim($dir, '/') . '/' . trim($node, '/') . '/');
				}
				
			}
			
			return $contents;
			
		}
		
        /**
         * Make human readable filesize string
         * 
         * @param int $bytes 
         * @param int $decimals  
         * 
         * @return <type>
         */
			public function fileSizeHuman(int $bytes, int $decimals = 2): ?string
			{
			  $sz = 'BKMGTP';
			  $factor = floor((strlen($bytes) - 1) / 3);
			  return sprintf("%.{$decimals}f", $bytes / pow(1024, $factor)) . @$sz[$factor];
			}
		
        /**
         * Make a new directory within a folder, with a clean name
         * 
         * @param string $folderName 
         * @param string $directory 
         * 
         * @return bool
         */
			public function makeNewDirectory(string $folderName, string $directory): bool
			{
				
					//clean folder name
					$folderName = preg_replace('#[^0-9a-zA-Z\_\-\.]#', '', $folderName);
					
					if (!is_dir($directory.$folderName)){
						
						mkdir($directory.$folderName);
						
						return true;
						
					} else {
						return false;
					}
				
			}
		
        /**
         * Delete a directory
         * 
         * @param string $folder 
         * 
         * @return bool
         */
			public function deleteFolder(string $folder): bool
			{
				
				//get directory contents
				$directoryArray = @scandir($folder);
				
				if ($directoryArray === false){
					//not a directory
					return false;
				}
				
				//set up arrays for files and folders
				$fileArray = array();
				$folderArray = array();
				
				foreach ($directoryArray as $objectName){
					
					if (is_dir($folder.$objectName) && !preg_match('#^[\.]+$#', $objectName)){
						$folderArray[] = $objectName;
					} else {
						//if level up markers
						if (preg_match('#^[\.]+$#', $objectName)){
							//ignore
						} else {
							$fileArray[] = $objectName;
						}
					}
				}
							
				if (count($fileArray) == 1 && count($folderArray) == 0 && in_array('.htaccess', $fileArray)){
					//echo $folder.'.htaccess';
					unlink($folder.'.htaccess');
				}
				
				$delete = @rmdir($folder);
				if ($delete === false){
					return false;
				} else {
					
					return true;
				}
				
			}
		
        /**
         * Upload a file to a directory
         * 
         * 
         * @return Void
         */
		public function uploadFile(): void
		{
			
			// Make sure file is not cached (as it happens for example on iOS devices)
			header("Expires: Mon, 26 Jul 1997 05:00:00 GMT");
			header("Last-Modified: " . gmdate("D, d M Y H:i:s") . " GMT");
			header("Cache-Control: no-store, no-cache, must-revalidate");
			header("Cache-Control: post-check=0, pre-check=0", false);
			header("Pragma: no-cache");
			header('Content-Type: application/json');
			
			//any attacks or suspicious params?
				if (!isset($_FILES['fm_upload_file']['error']) || is_array($_FILES['fm_upload_file']['error'])){
					echo '{"error":true, "info": "Invalid Parameters."}';
					exit;
				}
			
			//any litteral errors
				switch ($_FILES['fm_upload_file']['error']) {
					case UPLOAD_ERR_OK:
						break;
					case UPLOAD_ERR_NO_FILE:
						$message =  'No file sent.';
						die('{"error":true, "info": "'.$message.'."}');
					case UPLOAD_ERR_INI_SIZE:
					case UPLOAD_ERR_FORM_SIZE:
						$message =  'Exceeded filesize limit.';
						die('{"error":true, "info": "'.$message.'."}');
					default:
						$message =  'Unknown errors.';
						die('{"error":true, "info": "'.$message.'."}');
				}
			
			//does it exceed a certain file size?
				if(!defined('MAX_UPLOAD_FILE_SIZE')){
					define('MAX_UPLOAD_FILE_SIZE',104857600 ); //100mb 
				}
				if ($_FILES['fm_upload_file']['size'] > MAX_UPLOAD_FILE_SIZE) {
					$message =  'Exceeded filesize limit.';
					die('{"error":true, "info": "'.$message.'."}');
				}
			
			//get chunk numbers, or if none sent then 0
				$chunk = isset($_REQUEST["chunk"]) ? intval($_REQUEST["chunk"]) : 0;
				$chunks = isset($_REQUEST["chunks"]) ? intval($_REQUEST["chunks"]) : 0;
			
			//eventual filename
				$fileName = $_POST['filename'];
				$directory = $_POST['directory'];
				
			//check ext first...
				$extBits = explode('.',$fileName);
				$fExt = end($extBits);
				$allowed = false;
				foreach($this->_allowed_file_types as $ext => $mime){
					if($ext === $fExt){
						$allowed = true;
						break;
					}
				}
				
				if($allowed === false){
					die('{"error":true, "info": "File type not allowed. '.htmlentities($fExt).'"}');
				}
			
			//chunk filename
				$chunkName = $fileName.'.part_'.$chunk;
				
			//chunk destinations
				$chunkFileDest = DOC_ROOT.'/temp/'.$chunkName;
				$tempFileDest = DOC_ROOT.'/temp/'.$fileName;
			
			//eventual destination
				
				$finalFileDest = urldecode($directory.$fileName);
			
			//move the chunk to the temp directory.
			if(move_uploaded_file($_FILES['fm_upload_file']['tmp_name'], $chunkFileDest) === true){
				
				//return if chunk successful, but not the last chunk.
				if($chunks > 0 && ($chunk < ($chunks-1))){
					
					die('{"error":false, "info":"Chunk Upload Successful.", "chunk": "'.$chunkName.'", "chunks":'.$chunks.'}');
					
				//if it's the last chunk (or only chunk)
				} else if($chunks == 0 || $chunk == ($chunks - 1)) {
					
					//open file to be written to
					$finalFileHandle = fopen($finalFileDest, 'wb');
					
					if($finalFileHandle !== false){
						
						//loop through chunks and make file.
						$i = 0;
						while($i <= ($chunks-1)){
							
							// Read binary input stream and append it to temp file
							$chunkFileHandle = fopen($tempFileDest.'.part_'.$i, "rb");
							
							if ($chunkFileHandle !== false) {
								
								while ($buff = fread($chunkFileHandle, 4096)){
									fwrite($finalFileHandle, $buff);
								}
								
								unlink($tempFileDest.'.part_'.$i);
								fclose($chunkFileHandle);
								
							} else {
								die('{"error":true, "info": "Failed to add chunk to main file."}');
							}
							
							$i++;
						}
						
						fclose($finalFileHandle);
						
						//check the mime type thoroughly
							$finfo = new \finfo(FILEINFO_MIME_TYPE);
							@$finfoFileMime = $finfo->file($finalFileDest);
							if ($finfoFileMime === false){
								$message =  'Invalid file format';
								die('{"error":true, "info": "'.$message.'."}');
							} else {
								
								//check file mime type and ext together with final file...
								//$fExt set above
								$allowed = false;
								$which = '';
								foreach($this->_allowed_file_types as $ext => $mime){
									if($fExt === $ext && $finfoFileMime == $mime){
										$allowed = true;
										break;
									}
								}
								
								if($allowed === false){
									unlink($finalFileDest);
									die('{"error":true, "info": "File ext / mime-type not allowed. '.htmlentities($fExt).' - '.htmlentities($finfoFileMime).'"}');
								}
								
							}
							
						//file complete.
						die('{"error":false, "info": "File Upload Successful.","directory":"'.$finalFileDest.'", "file": "'.$fileName.'", "chunks":'.$chunks.'}');
						
					} else {
						die('{"error":true, "info": "Failed to open final file."}');
					}
					
				}
			} else {
				$message =  'Failed to move chunk.';
				die('{"error":true, "info": "'.$message.'."}');
			}
			
		}
		
		public function isImage($file){
			
			$nameTemp = explode(".", $file);
			$extension = strtolower(end($nameTemp));
			
			if ($extension == 'jpg' ||$extension == 'jpeg' ||$extension == 'png' || $extension == 'gif' || $extension == 'bmp'){
				return true;
			} else {
				return false;
			}
			
		}
		
